dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
        ajax: '../../../../examples/ajax/data/objects.txt',
        columns: [
            { data: 'id' },
            { data: 'name' },
            { data: 'position' },
            { data: 'office' },
            { data: 'extn' },
            { data: 'start_date' },
            { data: 'salary' }
        ],
		rowReorder: {
            dataSrc: 'id'
        }
	} );
} );
