dt-example.js:
// Set the RowReorder defaults
$.extend(DataTable.RowReorder.defaults, { selector: 'tr' });

// Enable RowReorder by default
DataTable.defaults.rowReorder = true;

// Initialise DataTable
$('#example').DataTable();

dt-example.js-vanilla:
// Set the RowReorder defaults
Object.assign(DataTable.RowReorder.defaults, { selector: 'tr' });

// Enable RowReorder by default
DataTable.defaults.rowReorder = true;

// Initialise DataTable
new DataTable('#example');
